import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthContext from '../AuthContext'; // Ensure AuthContext is set up
import './Dashboard.css'; // Import your CSS for styling

const Dashboard = () => {
  const { user, logout } = useContext(AuthContext); // Using context for user and logout
  const navigate = useNavigate();

  // If no user is logged in, redirect to login page
  if (!user) {
    navigate('/login');
    return null;
  }

  // Handle user logout
  const handleLogout = () => {
    logout(); // Call logout function to clear the session
    navigate('/login'); // Navigate back to login page after logout
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-box">
        <h2>Welcome, {user.email}</h2>
        <p>You are now logged in and can access your dashboard.</p>
        <button className="btn-logout" onClick={handleLogout}>
          Logout
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
