import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useContext } from 'react';
import AuthContext from '../AuthContext'; // Import AuthContext

const Login = () => {
  const { login } = useContext(AuthContext); // Use login function from context
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
  
    try {
      const response = await axios.post('http://localhost:3001/login', 
        { email, password },{
        headers:{
        'Content-Type': 'application/json'
        }
       
    });
  
      const token = response.data.token;  // Assuming the token is in the response body
      
      // Store the token in localStorage
      localStorage.setItem('token', token);
  
      // Optionally, you can also store user data
      const userData = {
        email: response.data.email,
        // fullName: response.data.fullName,  // If you need to store more data
      };
      console.log(userData.email, 'userData');
      
      localStorage.setItem('user', JSON.stringify(userData));
  
      // Update context if you're using one
      login(userData);
  
      setLoading(false);
      navigate('/dashboard');
    } catch (err) {
      setLoading(false);
      setError('Invalid credentials');
    }
  };
  

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg w-96">
        <h2 className="text-2xl font-semibold text-center mb-4">Login</h2>
        <form onSubmit={handleLogin}>
          <div className="mb-4">
            <input 
              type="email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              placeholder="Email" 
              required 
              className="w-full p-3 border border-gray-300 rounded-md"
            />
          </div>
          <div className="mb-4">
            <input 
              type="password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              placeholder="Password" 
              required 
              className="w-full p-3 border border-gray-300 rounded-md"
            />
          </div>
          <button 
            type="submit" 
            className="w-full p-3 bg-blue-500 text-white rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>
        {error && <p className="text-red-500 text-center mt-4">{error}</p>}
      </div>
    </div>
  );
};

export default Login;
