import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthContext from '../AuthContext'; // Ensure AuthContext is set up
import './Dashboard.css'; // Import your CSS for additional styling

const Dashboard = () => {
  const { user, logout } = useContext(AuthContext); // Using context for user and logout
  console.log(user,'user');
  
  const navigate = useNavigate();

  // If no user is logged in, redirect to login page
  if (!user) {
    navigate('/login');
    return null;
  }

  // Handle user logout
  const handleLogout = () => {
    logout(); // Call logout function to clear the session
    navigate('/login'); // Navigate back to login page after logout
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg w-96">
        <h2 className="text-2xl font-semibold text-center mb-4">
          Welcome, {user.email || user.Name} {/* Greet user by name, or email if name is not available */}
        </h2>
        <p className="text-gray-600 text-center mb-6">
          You are now logged in and can access your dashboard.
        </p>
        <div className="flex justify-center">
          <button 
            className="py-2 px-6 bg-blue-500 text-white rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
            onClick={handleLogout}
          >
            Logout
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
