import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion'; // Import framer-motion for animations
import AuthContext from '../AuthContext'; // Ensure AuthContext is set up
import './Dashboard.css'; // Import the updated CSS file

const Dashboard = () => {
  const { user, logout } = useContext(AuthContext); // Using context for user and logout
  const navigate = useNavigate();

  // If no user is logged in, redirect to login page
  if (!user) {
    navigate('/login');
    return null;
  }

  // Handle user logout
  const handleLogout = () => {
    logout(); // Call logout function to clear the session
    localStorage.removeItem('token'); // Remove token from localStorage
    navigate('/login'); // Navigate back to login page after logout
  };

  // Navigate to the ProductList page
  const goToProducts = () => {
    navigate('/product'); // This will take the user to the /products route
  };

  // Handle Add Product page navigation
  const handleAddProduct = () => {
    navigate('/add-product'); // This will take the user to the /products/add route
  };

  return (
    <motion.div
      className="dashboard-container"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
    >
      <motion.div
        className="dashboard-card"
        initial={{ y: -50 }}
        animate={{ y: 0 }}
        transition={{ duration: 0.7 }}
      >
        <h2 className="greeting">
          Welcome, {user.email || 'User'} {/* Greet by email */}
        </h2>
        <p className="dashboard-info">
          You are now logged in and can access your dashboard.
        </p>

        {/* Button to navigate to Products page */}
        <div className="navigate-btn-container">
          <button 
            className="navigate-btn"
            onClick={goToProducts} // Trigger navigation to products page
          >
            View Products
          </button>
        </div>

        <div className="logout-btn-container">
          <button 
            className="logout-btn"
            onClick={handleLogout}
          >
            Logout
          </button>
        </div>

        <button
          className="add-product-btn"
          onClick={handleAddProduct}
        >
          Add Product
        </button>
      </motion.div>
    </motion.div>
  );
};

export default Dashboard;
