import React, { useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import AuthContext from '../AuthContext';
import { FaBoxOpen, FaPlusCircle, FaSignOutAlt, FaUserCircle } from 'react-icons/fa';
import axios from 'axios';

const Dashboard = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const [productCount, setProductCount] = React.useState(0);

  useEffect(() => {
    // Fetch product count
    axios.get('http://localhost:3001/product')
      .then(res => setProductCount(res.data.length))
      .catch(err => console.error('Error fetching product count', err));
  }, []);

  if (!user) {
    navigate('/login');
    return null;
  }

  const handleLogout = () => {
    logout();
    localStorage.removeItem('token');
    navigate('/login');
  };

  const goToProducts = () => navigate('/product');
  const handleAddProduct = () => navigate('/addproduct');

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-100 flex flex-col">
      {/* Navbar */}
      <nav className="bg-white shadow-md px-6 py-4 flex justify-between items-center">
        <div className="flex items-center gap-2 text-blue-800 text-xl font-semibold">
          <FaUserCircle className="text-2xl" />
          {user?.email || 'User'}
        </div>
        <button
          onClick={handleLogout}
          className="flex items-center gap-2 bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded transition"
        >
          <FaSignOutAlt />
          Logout
        </button>
      </nav>

      {/* Dashboard Content */}
      <motion.div
        className="flex-grow flex flex-col items-center justify-center px-6 py-10"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.6 }}
      >
        <motion.div
          className="bg-white shadow-2xl rounded-3xl p-8 w-full max-w-xl text-center"
          initial={{ y: -20 }}
          animate={{ y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <h2 className="text-3xl font-bold text-blue-700 mb-2">Welcome Back!</h2>
          <p className="text-gray-600 mb-6">Manage your products efficiently</p>

          {/* Stats */}
          <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
            <p className="text-lg font-semibold text-blue-800">Total Products: <span className="font-bold">{productCount}</span></p>
          </div>

          {/* Action Buttons */}
          <div className="space-y-4">
            <motion.button
              whileTap={{ scale: 0.97 }}
              onClick={goToProducts}
              className="w-full flex items-center justify-center gap-2 bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-md shadow transition duration-300"
            >
              <FaBoxOpen />
              View Products
            </motion.button>

            <motion.button
              whileTap={{ scale: 0.97 }}
              onClick={handleAddProduct}
              className="w-full flex items-center justify-center gap-2 bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded-md shadow transition duration-300"
            >
              <FaPlusCircle />
              Add Product
            </motion.button>
          </div>
        </motion.div>
      </motion.div>
    </div>
  );
};

export default Dashboard;
